    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            Model.Koneksi.st.executeUpdate("update dasar_spt set nomor_surat='" + tfNomor.getText() + "',dasar1='" + tfdasar1.getText() + "',dasar2='" + tfdasar2.getText() + "' where id = 2");
            JOptionPane.showMessageDialog(null, "Berhasil merubah ");
            vSPT.tfNomorSurat.setText(tfNomor.getText().toString());
            vSPT.taDasar1.setText(tfdasar1.getText().toString());
            vSPT.taDasar2.setText(tfdasar2.getText().toString());
        dispose();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Eror Hapus : " + e);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

