    private void tampilDasar() {
        try {
            ResultSet rs = Model.Koneksi.st.executeQuery("select * from dasar_spt");
            while (rs.next()) {
                tfNomor.setText(rs.getString(2));
                tfdasar1.setText(rs.getString(3));
                tfdasar2.setText(rs.getString(4));
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "eror tampil : " + e);
        }
    }
