    private void btnBaruActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBaruActionPerformed
        btnSimpan.setEnabled(true);
        btnEdit.setEnabled(false);
        btnHapus.setEnabled(false);
        tfNama.setEnabled(true);
        tfNIP.setEnabled(true);
        tfJabatan.setEnabled(true);
        bersih();
        kode();
    }//GEN-LAST:event_btnBaruActionPerformed

    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
        int row = jTable1.getSelectedRow();
        if (jTable1.getSelectedColumnCount() == 1) {
            tfKode.setText(jTable1.getValueAt(row, 0).toString());
            tfNama.setText(jTable1.getValueAt(row, 1).toString());
            tfJabatan.setText(jTable1.getValueAt(row, 2).toString());
            tfNIP.setText(jTable1.getValueAt(row, 3).toString());
            btnBaru.setEnabled(true);
            btnEdit.setEnabled(true);
            btnHapus.setEnabled(true);
            btnSimpan.setEnabled(false);

            tfNama.setEnabled(true);
            tfNIP.setEnabled(true);
            tfJabatan.setEnabled(true);
        }
    }//GEN-LAST:event_jTable1MouseClicked

    private void btnEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditActionPerformed
        try {
            if (tfJabatan.getText().equals("") && tfNIP.getText().equals("")) {
                tfJabatan.setText("-");
                tfNIP.setText("-");
            }
            Model.Koneksi.st.executeUpdate("update tbl_kepala set nama='" + tfNama.getText() + "',jabatan='" + tfJabatan.getText() + "',nip='" + tfNIP.getText() + "' where kode_kepala='" + tfKode.getText() + "'");
            JOptionPane.showMessageDialog(null, "Berhasil merubah ");
            tampilKepala();
            aturTombol(false);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Eror Hapus : " + e);
        }
    }//GEN-LAST:event_btnEditActionPerformed

    private void btnHapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHapusActionPerformed
        try {
            Model.Koneksi.st.executeUpdate("delete from tbl_kepala where kode_kepala='" + tfKode.getText() + "'  ");

            JOptionPane.showMessageDialog(null, "Berhasil menghapus ");
            tampilKepala();
            aturTombol(false);
            kode();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Eror Hapus : " + e);
        }
    }//GEN-LAST:event_btnHapusActionPerformed

    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        try {
            if (tfJabatan.getText().equals("") && tfNIP.getText().equals("")) {
                tfJabatan.setText("-");
                tfNIP.setText("-");
            }
            if (cekData() == true) {
                Model.Koneksi.st.executeUpdate("insert into tbl_kepala values (" + 0 + ",'" + tfKode.getText() + "','" + tfNama.getText() + "','" + tfJabatan.getText() + "','" + tfNIP.getText() + "')");
                JOptionPane.showMessageDialog(null, "Berhasil menyimpan");
                tampilKepala();
                aturTombol(false);
                kode();

                tfNama.setEnabled(false);
                tfNIP.setEnabled(false);
                tfJabatan.setEnabled(false);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "eror Simpan : " + e);
        }

    }//GEN-LAST:event_btnSimpanActionPerformed

