    private void headerTable() {
        model = new DefaultTableModel();
        model.addColumn("Kode");
        model.addColumn("Nama");
        model.addColumn("Jabatan");
        model.addColumn("NIP");
        jTable1.setModel(model);
    }

    private void tampilKepala() {
        model.getDataVector().removeAllElements();
        try {
            ResultSet rs = Model.Koneksi.st.executeQuery("select * from tbl_kepala");
            while (rs.next()) {
                Object[] data = new Object[6];
                data[0] = (rs.getString(2));
                data[1] = (rs.getString(3));
                data[2] = (rs.getString(4));
                data[3] = (rs.getString(5));
                model.addRow(data);

            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "eror tampil : " + e);
        } //ini blok catch
        bersih();
    }

    private void aturTombol(boolean b) {
        tfKode.setEnabled(false);
        btnBaru.setEnabled(true);
        btnEdit.setEnabled(b);
        btnHapus.setEnabled(b);
        btnSimpan.setEnabled(b);
    }

    private void bersih() {
        tfKode.setText("");
        tfNIP.setText("");
        tfNama.setText("");
        tfJabatan.setText("");
    }

    private void kode() {
        System.out.println();
        String splits;
        for (int i = 0; i < model.getRowCount(); i++) {
            splits = jTable1.getValueAt(i, 0).toString();
            String[] parts = splits.split("-");

            if (Integer.parseInt(parts[1]) != i + 1) {
                System.out.println(Integer.parseInt(parts[1]));
                if (i < 10) {
                    tfKode.setText("KP-0" + Integer.toString(i + 1));
                    break;
                } else {
                    tfKode.setText("KP-" + Integer.toString(i + 1));
                    break;
                }
            }

            if ((model.getRowCount() + 1) < 10) {
                tfKode.setText("KP-0" + Integer.toString(model.getRowCount() + 1));
            } else {
                tfKode.setText("KP-" + Integer.toString(model.getRowCount() + 1));
            }
        }

    }

    private boolean cekData() {
        boolean cek = true;
        if (tfNama.getText().equals("")) {
            cek = false;
        }
        if (cek == false) {
            JOptionPane.showMessageDialog(null, "Tolong Nama dan Jabatan Harus diIsi");
        }
        return cek;
    }

