    private void Jabatan() {
        if (cbPegawai.getSelectedItem() == "-Silahkan Pilih Nama Konsultan-") {
            tfJabatan.setText("-");
        } else {
            String b = null, c = null, d = null;
            try {
                ResultSet rs = Model.Koneksi.st.executeQuery("select jabatan, golongan, tingkatan from tbl_konsultan where nama = '" + cbPegawai.getSelectedItem() + "'");
                while (rs.next()) {
                    String a = rs.getString(1);
                    b = a;
                    a = rs.getString("golongan");
                    c = a;
                    a = rs.getString("tingkatan");
                    d = a;
                }
                tfJabatan.setText(b);
                tfGolongan.setText(c);
                tfTingkat.setText(d);
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "eror dasar : " + e);
            }

        }
    }

