    private void simpanSPD() {
        try {
            Date date = jDateBerangkat.getDate();
            Date date1 = jDateKembali.getDate();
            Date date2 = tfPadaTgl.getDate();
            String strDate = String.format("%1$tY-%1$tm-%1$td", date);
            String strDate2 = String.format("%1$tY-%1$tm-%1$td", date1);
            String strDate3 = String.format("%1$tY-%1$tm-%1$td", date2);
            //  System.out.println(cbKonsultan1.getSelectedIndex());
            Model.Koneksi.st.executeUpdate("insert  into `tbl_surat_spd` values ('" + id_spd.getText() + "','" + tfNomorSPD.getText() + "','" + taPenjabat.getText() + "'," + cbPegawai.getSelectedIndex() + ",'" + taMaksud.getText() + "','" + tfKendaraan.getText() + "','" + tfTempatBerangkat.getText() + "','" + tfTempatTujuan.getText() + "','" + tfLamaPer.getText() + "','" + strDate + "','" + strDate2 + "','" + tfNamaInstansi.getText() + "','" + tfKodeF.getText() + "','" + tfKodeK.getText() + "','" + tfKodeA.getText() + "','" + tfKetLala.getText() + "','" + tfDiTetap.getText() + "','" + strDate3 + "'," + cbPenjabat.getSelectedIndex() + ")");
            JOptionPane.showMessageDialog(null, "Berhasil menyimpan");
            //id.setText(strDate);
            CetakSPT();

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "eror Simpan : " + e);
        }
    }

