    private void dasarnomor() {
        try {
            ResultSet rs = Model.Koneksi.st.executeQuery("select * from dasar_spt");
            while (rs.next()) {
                tfNomorSurat.setText(rs.getString(2));
                taDasar1.setText(rs.getString(3));
                taDasar2.setText(rs.getString(4));
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "eror dasar : " + e);
        }
    }

    private void DataComboBoxKonsultan() {
        try {
            ResultSet rs = Model.Koneksi.st.executeQuery("select nama from tbl_konsultan");
            while (rs.next()) {
                cbKonsultan1.addItem(rs.getString("nama"));
                cbKonsultan2.addItem(rs.getString("nama"));
                cbKonsultan3.addItem(rs.getString("nama"));
                cbKonsultan4.addItem(rs.getString("nama"));
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "eror dasar : " + e);
        }
    }

    private void DataComboBoxKepala() {
        try {
            ResultSet rs = Model.Koneksi.st.executeQuery("select nama from tbl_kepala");
            while (rs.next()) {
                cbKepalaDinas.addItem(rs.getString("nama"));
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "eror dasar : " + e);
        }
    }

    private void Besih() {
        cbKonsultan1.setSelectedIndex(0);
        cbKonsultan2.setSelectedIndex(0);
        cbKonsultan3.setSelectedIndex(0);
        cbKonsultan4.setSelectedIndex(0);
        cbKepalaDinas.setSelectedIndex(0);
        tfTempat.setText("Bandar Lampung");
        dateUntuk.setDate(Date.from(Instant.now()));
        jDateChooser1.setDate(Date.from(Instant.now()));
        jTextField1.setText("Bandar Lampung");
    }

    private void simpanSPT() {
        try {
            Date date = jDateChooser1.getDate();
            Date date1 = dateUntuk.getDate();
            String strDate = String.format("%1$tY-%1$tm-%1$td", date);
            String strDate2 = String.format("%1$tY-%1$tm-%1$td", date1);
            //  System.out.println(cbKonsultan1.getSelectedIndex());
            Model.Koneksi.st.executeUpdate("insert into tbl_surat_spt values (" + 0 + ",'"+id.getText()+"','" + tfNomorSurat.getText() + "','" + taDasar1.getText() + "','" + taDasar2.getText() + "','" + tfTempat.getText() + "','" + strDate2 + "','" + jTextField1.getText() + "'," + cbKonsultan1.getSelectedIndex() + "," + cbKonsultan2.getSelectedIndex() + "," + cbKonsultan3.getSelectedIndex() + "," + cbKonsultan4.getSelectedIndex() + ",'" + strDate + "'," + cbKepalaDinas.getSelectedIndex() + ")");
            JOptionPane.showMessageDialog(null, "Berhasil menyimpan");
            //id.setText(strDate);
            
           

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "eror Simpan : " + e);
        }
    }

    private void CetakSPT() {
        LaporanSPT spt = new LaporanSPT();
        spt.paramSpt = "kode_spt";
        spt.idSpt = id.getText().toString();
        spt.cetakSPT();
    }

